ValidationRule.errorConditionFormula:
AND(
 NOT( $User.Bypass_VR__c ),
 OR(
   AND(
     INCLUDES( Type_de_service__c , "Transit") ,
     ISBLANK( Nombre_de_dossier_transit__c )
    ),
   AND(
     NOT(INCLUDES(Type_de_service__c, "Transit")),
     NOT(ISBLANK(Nombre_de_dossier_transit__c))
    )
  )
)
